import { Component } from "react";
import logo from "../images/logo-v1.png";
import $ from 'jquery';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import Close from '@material-ui/icons/Close';


import { faBars } from "@fortawesome/free-solid-svg-icons";


class Navbar extends Component{
    componentDidMount(){
        $(".link").click(function(){
            $('html, body').animate({
                scrollTop: $("#"+$(this).attr("datatype")).offset().top-100
              },500);
        })
        $(window).scroll(function(){
            if($(window).scrollTop()>250){
                $("nav").css({position:"fixed"})
            }
            else{
                $("nav").css({position:"relative"})
 
            }
        })
        $(window).resize(function(){
                if($(window).width()>768){
                    $(".nav-bar-mob").slideDown();
                    $("#bars").fadeOut();
                    $(".moon").fadeOut();
                }
                if($(window).width()<768){
                    $("#bars").fadeOut();
                    $(".moon").fadeIn();


                }
             
        })
        if($(window).width()<768){
            $(".nav-bar-mob").slideUp()

        }
        $("#bars").click(function(){
            $(".nav-bar-mob").slideDown();
            $("#bars").fadeOut();
            $(".moon").fadeIn();
        })
        $(".moon").click(function(){
            $(".nav-bar-mob").slideUp();
            $("#bars").fadeIn();
            $(".moon").fadeOut();
        })
        
    }

    render(){
        return(
       <nav>
           <div className="container">
               <div className="row">
               <div className="col-md-3">
                   <img src={logo}/>
               </div>
               <div className="col-md-9 " style={{position:"relative"}}>
                   <ul className="nav-bar-mob" >
                   <li className="link" datatype="home">
                       home
                       <span></span>
                       <span></span>
                       <span></span>
                       </li>
                   <li className="link" datatype="about">
                       about
                   <span></span>
                       <span></span>
                       <span></span>
                   </li>
                   <li className="link" datatype="service">
                       services
                       <span></span>
                       <span></span>
                       <span></span>
                       </li>
                   <li className="link" datatype="projects">
                       projects
                       <span></span>
                       <span></span>
                       <span></span>

                   </li>
                   <li className="link">
                       blog
                       <span></span>
                       <span></span>
                       <span></span>
                       </li>
                   <li className="link">
                       contact
                       <span></span>
                       <span></span>
                       <span></span>
                       
                   </li >
                   <li>free quote</li>
                   <li style={{opacity:"0"}}>blank</li>
                   </ul>
                   <FontAwesomeIcon icon={faBars} id="bars" />
                   <Close className="icon moon"/>

                   </div>
               </div>
               </div>
       </nav>
        )
    }
}
export default Navbar
