import React, { Component } from "react";
import Slider from "react-slick";
import projectContent from "./ProjectContent";
import bar from "../images/footer-bg.jpg";
import imageone from "../images/portfolio-01.jpg"
import imagetwo from "../images/portfolio-02.jpg"
import imagethree from "../images/portfolio-03.jpg"
import imagefour from "../images/portfolio-04.jpg"





export default class Resizable extends Component {

  constructor(props) {
    super(props);
    this.next = this.next.bind(this);
    this.previous = this.previous.bind(this);
  }

  next() {
    this.slider.slickNext();
  }
  previous() {
    this.slider.slickPrev();
  }
  state = {
    display: true,
    width: 600,
   info:[
    {h6:"Website Builder",p:"Marketing",image :imageone},
    {h6:"Website Builder",p:"Marketing",image :imageone},
    {h6:"Website Builder",p:"Marketing",image :imageone},
    {h6:"Website Builder",p:"Marketing",image :imageone},
    {h6:"Website Builder",p:"Marketing",image :imageone}
    ]
 


            
     
 
  };
  render() {
    const Product = this.state.info.map(function(i){
        return <projectContent 
        image={i.image}
        h6 = {i.h6}
        image = {i.p}
        />
     })

    const settings = {
      arrows:false,
      dots: false,
      infinite: true,
      speed: 500,
      slidesToShow: 5,
      slidesToScroll: 1,
      responsive: [
        {
          breakpoint: 1199,
          settings: {
            slidesToShow: 2,
            slidesToScroll: 1,
            infinite: true,
            dots: false
          }
        },
        {
          breakpoint: 991,
          settings: {
            slidesToShow: 1,
            slidesToScroll: 1,
            infinite: true,
            dots: false
          }
        },
        {
          breakpoint: 600,
          settings: {
            slidesToShow: 1,
            slidesToScroll: 1,
            initialSlide: 1
          }
        },
        {
          breakpoint: 480,
          settings: {
            slidesToShow: 1,
            slidesToScroll: 1
          }
        },
        {
          breakpoint: 395,
          settings: {
            slidesToShow: 1,
            slidesToScroll: 1
          }
        
        },
        {
          breakpoint: 320,
          settings: {
            slidesToShow: .95,
            slidesToScroll: 1
          }
        }
      ]
    };

    return (
   
     <div className="products">
         <div className="container">
<div className="row">
    <div className="col-sm-12">
         <h5>OUR PORTOFOLIO</h5>
         <h1>See Our Recent <span>Projects</span></h1>
         <img src={bar} style={{width:"7em",height:".3em"}}/>
       
     </div>
     
     </div>
     </div>
     <Slider {...settings}>
         {Product}
         
        </Slider>
     </div>

    );
  }
}
